import React, { Component } from 'react';
//import logo from '../../images/logo.svg';
import './Main.css';
import Form from '../Form/Form';
import App from '../App/App';
import Nickname from "../Nickname/Nickname";
import Footer from "../Footer";
import firebase from 'firebase';
import firebaseConfig from '../../config';
import axios from 'axios';
import dotenv from 'dotenv';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';


firebase.initializeApp(firebaseConfig);
class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: null,
            category: null,
            exists: null,
        }
    }

    componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
            this.setState({ user: user });
            this.doesUserExist();
            // this.props.history.push('/path');
        });
    }

    componentDidUpdate() {
        // console.log(this.state.exists.nickname);
        // console.log(this.state.user.nickname)
        if (this.state.exists) {
            if (this.state.exists.nickname != this.state.nickname) {
                console.log("REEEEE: " + this.state.exists.nickname);
                console.log("REEEEE: " + this.state.user.nickname)
                this.doesUserExist();
            }
        }
    }

    handleSignIn() {
        const provider = new firebase.auth.GoogleAuthProvider();
        firebase.auth().signInWithPopup(provider);
    }

    // renderRedirect = () => {
    //     return <Redirect to="/App" />
    // }

    handleLogOut() {
        firebase.auth().signOut();
    }

    chooseCategory1() {
        this.setState({ category: "random" });
    }

    chooseCategory2() {
        this.setState({ category: "disney" });
    }

    chooseCategory3() {
        this.setState({ category: "tvseries" });
    }

    chooseCategory4() {
        this.setState({ category: "games" });
    }

    doesUserExist() {
        console.log("doesUserExist - START");
        axios.get('/api/email/' //+ 'test1@gmail.com') 
            + this.state.user.email)
            .then(res => {
                const persons = res.data;
                console.log("success + " + persons);
                this.setState({ exists: persons });

                if (!persons) {
                    let body = {
                        email: this.state.user.email,
                        nickname: this.state.user.displayName
                    }
                    console.log("Body: " + JSON.stringify(body));
                    axios.post('/api/createUser', body)
                }
                console.log("Exists: " + this.state.exists);
            }).catch(err => {
                console.log("GET ERR: " + err);
                console.log("URI: " + process.env.MONGODB_URI);
            });


        // if (!this.state.exists) {
        //     let body = {
        //         email: this.state.user.email,
        //         nickname: this.state.user.displayName
        //     }
        //     console.log("Body: " + JSON.stringify(body));
        //     axios.post(process.env.MONGODB_URI + '/api/createUser', body)
        // }
    }

    render() {
        return (
            <div className="app">
                <div className="app__header">
                    {/* <img src={logo} className="app__logo" alt="logo" /> */}
                    {this.state.user ? (
                        // <button>
                        //     Change Nickname
                        // </button>
                        // { this.doesUserExist.bind(this) }


                        < h1>
                            {
                                this.state.exists ? (
                                    <div>Welcome, <span>{this.state.exists.nickname}</span></div>
                                ) : (<div>Welcome, <span>{this.state.user.displayName}</span></div>)
                            }

                        </h1>

                    ) : (
                            <h1>
                                CHAT SHROOMS
                            </h1>
                        )}


                    {this.state.user ? (
                        // <button>
                        //     Change Nickname
                        // </button>
                        <div>
                            <Nickname user={this.state.user} />

                        </div>

                    ) : (
                            <div></div>
                        )}



                </div>
                {
                    this.state.user ? (
                        <div className="app__list">
                            {console.log("User to Form: " + this.state.user.displayName)}
                            {console.log("Email to Form: " + this.state.user.email)}
                            {console.log("Category: " + this.state.category)}
                        </div>
                    ) : (
                            <span className="login-first">Please login first before proceeding with the chat</span>
                        )
                }
                {
                    this.state.user ? (
                        <div className="app__list">
                            {console.log("User to Form: " + this.state.user.displayName)}
                            {console.log("Email to Form: " + this.state.user.email)}
                            {console.log("Category: " + this.state.category)}
                            <span className="login-first">Please choose a category to proceed</span>
                            <div>
                                <button
                                    className="app__button rounded "
                                    onClick={this.chooseCategory1.bind(this)}
                                >
                                    RANDOM
                                </button>
                                <button
                                    className="app__button rounded "
                                    onClick={this.chooseCategory2.bind(this)}
                                >
                                    DISNEY
                                </button>
                                <button
                                    className="app__button rounded "
                                    onClick={this.chooseCategory3.bind(this)}
                                >
                                    TV SERIES
                                </button>
                                <button
                                    className="app__button rounded "
                                    onClick={this.chooseCategory4.bind(this)}
                                >
                                    GAMES
                                </button>
                            </div>
                        </div>
                    ) : (
                            <div></div>
                        )
                }
                {
                    this.state.category && this.state.user ? (
                        <div className="app__list">
                            {console.log("User to Form: " + this.state.user.displayName)}
                            {console.log("Email to Form: " + this.state.user.email)}
                            {console.log("Category: " + this.state.category)}
                            <Form user={this.state.user} category={this.state.category} />
                        </div>
                    ) : (
                            <div></div>
                        )
                }
                {
                    !this.state.user ? (
                        <div><button
                            className="app__button rounded "
                            onClick={this.handleSignIn.bind(this)}
                        >
                            Sign in
                            </button></div>
                    ) : (
                            // this.context.router.push("./App/App")

                            // <Router>
                            //     <Route exact path="/">
                            //         {console.log("We were here")}
                            //         {this.state.user ? <Redirect to="/App" /> : <Main />}
                            // </Route>
                            <div><button
                                className="app__button rounded "
                                onClick={this.handleLogOut.bind(this)}
                            >
                                Logout
                                </button></div>
                            // {/* </Router> */}
                            // <button
                            //     className="app__button rounded "
                            //     onClick={this.handleLogOut.bind(this)}
                            // >
                            //     Logout
                            //     </button>
                        )
                }
                <Footer />
            </div >
        );
    }
}
export default Main;
