import React, { Component } from 'react';
import './Message.css';

class Message extends Component {

    // findNickOfUser(){

    // }

    render() {
        return (
            <div className="msg">
                {this.props.dbdata ? (
                    <span className="author">
                        {this.props.dbdata}:
                        </span>
                ) : (
                        <span className="author">
                            {/* {this.props.message.userName}: */}
                            {this.props.nickDb ? (
                                <span>{this.props.nickDb.nickname}:</span>
                            ) : (
                                    <span></span>
                                )}

                        </span>
                    )
                }
                {this.props.message.message}
            </div>
        )
    }
}


export default Message;
