import React, { Component } from 'react';
import './Message.css';

class Message extends Component {
    render() {
        return (
            <div className="msg">
                <span className="author">

                    {this.props.dbdata ? (
                        <h1>{this.props.dbdata}:</h1>
                    ) : (
                            <h1>{this.props.message.userName}:</h1>
                        )
                    }





                </span>
                {this.props.message.message}
            </div>
        )
    }
}


export default Message;
